$ser_content = 'a:2:{s:7:"CONTENT";s:0:"";s:4:"VARS";a:2:{s:4:"data";a:3:{i:26459;a:5:{s:15:"DETAIL_PAGE_URL";s:24:"/patients/consult/26459/";s:16:"isAnswererClinic";b:0;s:8:"answerer";a:4:{s:5:"image";a:18:{s:8:"ORIGINAL";s:52:"/upload/main/904/042A6510-kopiya-_1_-_1_-_1_-_1_.png";s:13:"webp_ORIGINAL";s:75:"/upload/resize_cache/main/904/0_0_1_95/042A6510-kopiya-_1_-_1_-_1_-_1_.webp";s:9:"100_100_P";s:75:"/upload/resize_cache/main/904/100_100_1/042A6510-kopiya-_1_-_1_-_1_-_1_.png";s:14:"webp_100_100_P";s:79:"/upload/resize_cache/main/904/100_100_1_95/042A6510-kopiya-_1_-_1_-_1_-_1_.webp";s:19:"quotas_registration";s:75:"/upload/resize_cache/main/904/190_200_1/042A6510-kopiya-_1_-_1_-_1_-_1_.png";s:24:"webp_quotas_registration";s:79:"/upload/resize_cache/main/904/190_200_1_95/042A6510-kopiya-_1_-_1_-_1_-_1_.webp";s:15:"specialist_list";s:75:"/upload/resize_cache/main/904/352_304_1/042A6510-kopiya-_1_-_1_-_1_-_1_.png";s:20:"webp_specialist_list";s:79:"/upload/resize_cache/main/904/352_304_1_95/042A6510-kopiya-_1_-_1_-_1_-_1_.webp";s:19:"specialist_list_big";s:52:"/upload/main/904/042A6510-kopiya-_1_-_1_-_1_-_1_.png";s:24:"webp_specialist_list_big";s:79:"/upload/resize_cache/main/904/568_548_1_95/042A6510-kopiya-_1_-_1_-_1_-_1_.webp";s:24:"specialist_detail_header";s:52:"/upload/main/904/042A6510-kopiya-_1_-_1_-_1_-_1_.png";s:29:"webp_specialist_detail_header";s:79:"/upload/resize_cache/main/904/448_471_1_95/042A6510-kopiya-_1_-_1_-_1_-_1_.webp";s:26:"specialist_detail_schedule";s:75:"/upload/resize_cache/main/904/320_436_1/042A6510-kopiya-_1_-_1_-_1_-_1_.png";s:31:"webp_specialist_detail_schedule";s:79:"/upload/resize_cache/main/904/320_436_1_95/042A6510-kopiya-_1_-_1_-_1_-_1_.webp";s:20:"slider_photo_preview";s:75:"/upload/resize_cache/main/904/438_264_1/042A6510-kopiya-_1_-_1_-_1_-_1_.png";s:25:"webp_slider_photo_preview";s:79:"/upload/resize_cache/main/904/438_264_1_95/042A6510-kopiya-_1_-_1_-_1_-_1_.webp";s:17:"slider_popup_full";s:52:"/upload/main/904/042A6510-kopiya-_1_-_1_-_1_-_1_.png";s:22:"webp_slider_popup_full";s:80:"/upload/resize_cache/main/904/1024_787_1_95/042A6510-kopiya-_1_-_1_-_1_-_1_.webp";}s:4:"name";s:46:"Корнеев Игорь Алексеевич";s:8:"position";s:161:"Главный врач, врач уролог-андролог высшей категории, доктор медицинских наук, профессор";s:2:"id";s:2:"13";}s:8:"question";a:7:{s:5:"title";s:12:"Вопрос";s:4:"name";s:12:"Оксана";s:5:"image";s:0:"";s:8:"position";s:0:"";s:4:"text";s:1177:"Добрый вечер. У мужа плохая спермограмма а-9, в-17, патологических 92%, лейкоцитов 2 млн. В прошлом году была проведена операция по удалению  варикоцелле методом Мармара. Так же при узи обнаружили хронический простатит с небольшим камнем. У нас в городе хороших андрологов нет, для лечения простатита ничего назначено не было. Причину такой плохой спермограммы назвать не могут.
Анализ на гормоны делали тестостерон 17 нмоль/л, эстрадиол 73 пг/мл, ЛГ 6 мме/мл, Фсг 3.98 мме/мл, пролактин 173 мме/л.так же делали анализ на кариотип, он в норме. Мои гормоны все в норме. Трубы проходимы. Подскажите, какая может быть причина такой плохой спермограммы. Мужу 32 года.";s:5:"files";s:33:"Прикреплены файлы";s:4:"date";s:21:"1 декабря 2019";}s:6:"answer";a:8:{s:5:"title";s:10:"Ответ";s:4:"name";s:46:"Корнеев Игорь Алексеевич";s:5:"image";a:18:{s:8:"ORIGINAL";s:52:"/upload/main/904/042A6510-kopiya-_1_-_1_-_1_-_1_.png";s:13:"webp_ORIGINAL";s:75:"/upload/resize_cache/main/904/0_0_1_95/042A6510-kopiya-_1_-_1_-_1_-_1_.webp";s:9:"100_100_P";s:75:"/upload/resize_cache/main/904/100_100_1/042A6510-kopiya-_1_-_1_-_1_-_1_.png";s:14:"webp_100_100_P";s:79:"/upload/resize_cache/main/904/100_100_1_95/042A6510-kopiya-_1_-_1_-_1_-_1_.webp";s:19:"quotas_registration";s:75:"/upload/resize_cache/main/904/190_200_1/042A6510-kopiya-_1_-_1_-_1_-_1_.png";s:24:"webp_quotas_registration";s:79:"/upload/resize_cache/main/904/190_200_1_95/042A6510-kopiya-_1_-_1_-_1_-_1_.webp";s:15:"specialist_list";s:75:"/upload/resize_cache/main/904/352_304_1/042A6510-kopiya-_1_-_1_-_1_-_1_.png";s:20:"webp_specialist_list";s:79:"/upload/resize_cache/main/904/352_304_1_95/042A6510-kopiya-_1_-_1_-_1_-_1_.webp";s:19:"specialist_list_big";s:52:"/upload/main/904/042A6510-kopiya-_1_-_1_-_1_-_1_.png";s:24:"webp_specialist_list_big";s:79:"/upload/resize_cache/main/904/568_548_1_95/042A6510-kopiya-_1_-_1_-_1_-_1_.webp";s:24:"specialist_detail_header";s:52:"/upload/main/904/042A6510-kopiya-_1_-_1_-_1_-_1_.png";s:29:"webp_specialist_detail_header";s:79:"/upload/resize_cache/main/904/448_471_1_95/042A6510-kopiya-_1_-_1_-_1_-_1_.webp";s:26:"specialist_detail_schedule";s:75:"/upload/resize_cache/main/904/320_436_1/042A6510-kopiya-_1_-_1_-_1_-_1_.png";s:31:"webp_specialist_detail_schedule";s:79:"/upload/resize_cache/main/904/320_436_1_95/042A6510-kopiya-_1_-_1_-_1_-_1_.webp";s:20:"slider_photo_preview";s:75:"/upload/resize_cache/main/904/438_264_1/042A6510-kopiya-_1_-_1_-_1_-_1_.png";s:25:"webp_slider_photo_preview";s:79:"/upload/resize_cache/main/904/438_264_1_95/042A6510-kopiya-_1_-_1_-_1_-_1_.webp";s:17:"slider_popup_full";s:52:"/upload/main/904/042A6510-kopiya-_1_-_1_-_1_-_1_.png";s:22:"webp_slider_popup_full";s:80:"/upload/resize_cache/main/904/1024_787_1_95/042A6510-kopiya-_1_-_1_-_1_-_1_.webp";}s:8:"position";s:161:"Главный врач, врач уролог-андролог высшей категории, доктор медицинских наук, профессор";s:4:"text";s:646:"Здравствуйте. Спермограмму не следует считать плохой, при таких показателях возможно зачатие естественным путем. Если этого не произошло в течение года регулярной половой жизни (2-4 половых акта в неделю), и гинеколог считает, что женского фактора бесплодия нет, Вам следует обратиться для лечения в центр репродуктивной медицины, например, к нам в МЦРМ. ";s:5:"files";s:0:"";s:4:"date";s:21:"1 декабря 2019";s:13:"specialist_id";s:2:"13";}}i:4982;a:5:{s:15:"DETAIL_PAGE_URL";s:23:"/patients/consult/4982/";s:16:"isAnswererClinic";b:1;s:8:"answerer";a:4:{s:5:"image";a:2:{s:8:"ORIGINAL";s:29:"/upload/images/logo-short.png";s:7:"resized";s:29:"/upload/images/logo-short.png";}s:4:"name";s:8:"МЦРМ";s:8:"position";s:0:"";s:2:"id";s:0:"";}s:8:"question";a:7:{s:5:"title";s:12:"Вопрос";s:4:"name";s:16:"Ангелина";s:5:"image";s:0:"";s:8:"position";s:0:"";s:4:"text";s:750:"Здравствуйте, уважаемый Андрей Адольфович! У меня есть направление на ЭКО по ОМС в ваш центр. Я сама медик и по рекомендации Ляшенко И.В. очень хотела бы попасть именно к Вам! В минздраве мне озвучили, что мне нужно самой сделать электронную карту и отправить в ваш центр. И есть ли какая-то очередность для пациентов на ЭКО по системе ОМС? Посоветуйте пожалуйста с чего мне начать? Заранее благодарна Вам за ответ.";s:5:"files";s:0:"";s:4:"date";s:22:"20 декабря 2013";}s:6:"answer";a:8:{s:5:"title";s:10:"Ответ";s:4:"name";s:8:"МЦРМ";s:5:"image";a:2:{s:8:"ORIGINAL";s:29:"/upload/images/logo-short.png";s:7:"resized";s:29:"/upload/images/logo-short.png";}s:8:"position";s:0:"";s:4:"text";s:213:"У нас нет очередности на проведение ЭКО. Что касается организации процесса, то следуйте подсказкам своего Минздрава";s:5:"files";s:0:"";s:4:"date";s:22:"20 декабря 2013";s:13:"specialist_id";s:0:"";}}i:16307;a:5:{s:15:"DETAIL_PAGE_URL";s:24:"/patients/consult/16307/";s:16:"isAnswererClinic";b:0;s:8:"answerer";a:4:{s:5:"image";a:18:{s:8:"ORIGINAL";s:29:"/upload/main/995/Soboleva.png";s:13:"webp_ORIGINAL";s:52:"/upload/resize_cache/main/995/0_0_1_95/Soboleva.webp";s:9:"100_100_P";s:52:"/upload/resize_cache/main/995/100_100_1/Soboleva.png";s:14:"webp_100_100_P";s:56:"/upload/resize_cache/main/995/100_100_1_95/Soboleva.webp";s:19:"quotas_registration";s:52:"/upload/resize_cache/main/995/190_200_1/Soboleva.png";s:24:"webp_quotas_registration";s:56:"/upload/resize_cache/main/995/190_200_1_95/Soboleva.webp";s:15:"specialist_list";s:52:"/upload/resize_cache/main/995/352_304_1/Soboleva.png";s:20:"webp_specialist_list";s:56:"/upload/resize_cache/main/995/352_304_1_95/Soboleva.webp";s:19:"specialist_list_big";s:29:"/upload/main/995/Soboleva.png";s:24:"webp_specialist_list_big";s:56:"/upload/resize_cache/main/995/568_548_1_95/Soboleva.webp";s:24:"specialist_detail_header";s:29:"/upload/main/995/Soboleva.png";s:29:"webp_specialist_detail_header";s:56:"/upload/resize_cache/main/995/448_471_1_95/Soboleva.webp";s:26:"specialist_detail_schedule";s:52:"/upload/resize_cache/main/995/320_436_1/Soboleva.png";s:31:"webp_specialist_detail_schedule";s:56:"/upload/resize_cache/main/995/320_436_1_95/Soboleva.webp";s:20:"slider_photo_preview";s:52:"/upload/resize_cache/main/995/438_264_1/Soboleva.png";s:25:"webp_slider_photo_preview";s:56:"/upload/resize_cache/main/995/438_264_1_95/Soboleva.webp";s:17:"slider_popup_full";s:29:"/upload/main/995/Soboleva.png";s:22:"webp_slider_popup_full";s:57:"/upload/resize_cache/main/995/1024_787_1_95/Soboleva.webp";}s:4:"name";s:48:"Соболева Елена Леонидовна";s:8:"position";s:99:"Врач-эндокринолог, доктор медицинских наук, профессор";s:2:"id";s:2:"31";}s:8:"question";a:7:{s:5:"title";s:12:"Вопрос";s:4:"name";s:18:"Анастасия";s:5:"image";s:0:"";s:8:"position";s:0:"";s:4:"text";s:2548:"Добрый день! НА протяжении 6 лет то худею, то набираю вес. Были строгие диеты (даже назову голодовками, не ела жирную еду, пропали кд на месяца 3, решила отожраться, отожралась, кд пришли ) Года 2 назад набрала вес + 15 кг за пол года. Изначально я была 65 кг при росте 162см, набрала до 80 кг. В весе 74 начала болеть грудь незначительно за 3 дня до кд. Постепенно я набирала вес и боль в груди увеличивалась. Начала пить по рекомендации гинеколога ОК, чтобы убрать прыщи. Меня хватило на 3 месяца, были мигрени, боль в груди еще больше стала, грудь налилась как ягода малина, бросила таблетки. Сбился цикл. Теперь грудь болит после овуляции, увеличивается в размере, очень чувствительная, невыносимо больно. Вылазят высыпания на груди, спине, лице. Головные боли, мигрени. Сильная раздражительность. Депрессия. Аппатия. Плаксивость. Сильная усталость. Чем ближе дни к КД, тем симптомы еще сильнее. Так же отеки по телу. Замечаю даже отеки на пальцах. Цикл 26-35 дней, постоянно разный. За день до КД вздутие живота. Первый день кд очень болезненный. Гинекологи сказали похудеть и все будет окей. Я похудела на 12 кг в течение года. Рост 162см, 68 кг (Но уже в моём рационе присутствовало много жиров, сладостей, белков) Симптомы какие были такие и остались. Для меня такое состояние дурость, а гинекологи говорят норма. Так вот я думаю что своими худениями и наборами веса я прибила гармональный фон. Думаю сдать анализы. Но какие? ";s:5:"files";s:0:"";s:4:"date";s:19:"9 января 2019";}s:6:"answer";a:8:{s:5:"title";s:10:"Ответ";s:4:"name";s:48:"Соболева Елена Леонидовна";s:5:"image";a:18:{s:8:"ORIGINAL";s:29:"/upload/main/995/Soboleva.png";s:13:"webp_ORIGINAL";s:52:"/upload/resize_cache/main/995/0_0_1_95/Soboleva.webp";s:9:"100_100_P";s:52:"/upload/resize_cache/main/995/100_100_1/Soboleva.png";s:14:"webp_100_100_P";s:56:"/upload/resize_cache/main/995/100_100_1_95/Soboleva.webp";s:19:"quotas_registration";s:52:"/upload/resize_cache/main/995/190_200_1/Soboleva.png";s:24:"webp_quotas_registration";s:56:"/upload/resize_cache/main/995/190_200_1_95/Soboleva.webp";s:15:"specialist_list";s:52:"/upload/resize_cache/main/995/352_304_1/Soboleva.png";s:20:"webp_specialist_list";s:56:"/upload/resize_cache/main/995/352_304_1_95/Soboleva.webp";s:19:"specialist_list_big";s:29:"/upload/main/995/Soboleva.png";s:24:"webp_specialist_list_big";s:56:"/upload/resize_cache/main/995/568_548_1_95/Soboleva.webp";s:24:"specialist_detail_header";s:29:"/upload/main/995/Soboleva.png";s:29:"webp_specialist_detail_header";s:56:"/upload/resize_cache/main/995/448_471_1_95/Soboleva.webp";s:26:"specialist_detail_schedule";s:52:"/upload/resize_cache/main/995/320_436_1/Soboleva.png";s:31:"webp_specialist_detail_schedule";s:56:"/upload/resize_cache/main/995/320_436_1_95/Soboleva.webp";s:20:"slider_photo_preview";s:52:"/upload/resize_cache/main/995/438_264_1/Soboleva.png";s:25:"webp_slider_photo_preview";s:56:"/upload/resize_cache/main/995/438_264_1_95/Soboleva.webp";s:17:"slider_popup_full";s:29:"/upload/main/995/Soboleva.png";s:22:"webp_slider_popup_full";s:57:"/upload/resize_cache/main/995/1024_787_1_95/Soboleva.webp";}s:8:"position";s:99:"Врач-эндокринолог, доктор медицинских наук, профессор";s:4:"text";s:270:"Здравствуйте. Все интересующие Вас вопросы можно обсудить только на очной консультации. Обследование назначается индивидуально каждому пациенту.";s:5:"files";s:0:"";s:4:"date";s:19:"9 января 2019";s:13:"specialist_id";s:2:"31";}}}s:4:"path";s:18:"/patients/consult/";}}';
